import React, { useState } from 'react';
import styles from './Main.module.css';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    comment: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus(null);

    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Ошибка при отправке формы');
      }

      const data = await response.json();
      console.log('Успешно отправлено:', data);
      setSubmitStatus('success');
      setFormData({
        name: '',
        phone: '',
        comment: ''
      });
    } catch (error) {
      console.error('Ошибка:', error);
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <section id="contact" className={styles.contactSection}>
      <div className={styles.contactOverlay}></div>
      <div className={styles.contactContent}>
        <h2>ОСТАВЬТЕ ЗАЯВКУ — МЫ СВЯЖЕМСЯ С ВАМИ</h2>
        <p className={styles.contactSubtitle}>
          Хотите узнать больше о планировках, ценах и записаться на просмотр домов в нашем поселке?
          Просто заполните форму — наш менеджер свяжется с вами в ближайшее время и ответит на все вопросы.
        </p>
        
        <form onSubmit={handleSubmit} className={styles.contactForm}>
          <div className={styles.formGroup}>
            <label htmlFor="name">Имя</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Введите Ваше имя"
              value={formData.name}
              onChange={handleChange}
              required
              disabled={isSubmitting}
            />
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="phone">Телефон</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              placeholder="+7 (999) 999-99-99"
              value={formData.phone}
              onChange={handleChange}
              required
              disabled={isSubmitting}
            />
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="comment">Комментарий</label>
            <textarea
              id="comment"
              name="comment"
              placeholder="Введите комментарий / вопрос"
              value={formData.comment}
              onChange={handleChange}
              rows="4"
              disabled={isSubmitting}
            />
          </div>
          
          <div className={styles.privacyPolicy}>
            <p>
              Нажимая на кнопку «Отправить заявку» вы соглашаетесь с 
              <a href="/privacy"> Политикой конфиденциальности</a>
            </p>
          </div>
          
          <button 
            type="submit" 
            className={styles.submitButton}
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Отправка...' : 'Отправить заявку'}
          </button>

          {submitStatus === 'success' && (
            <div className={styles.successMessage}>
              Спасибо! Ваша заявка успешно отправлена.
            </div>
          )}
          
          {submitStatus === 'error' && (
            <div className={styles.errorMessage}>
              Произошла ошибка при отправке. Пожалуйста, попробуйте позже.
            </div>
          )}
        </form>
      </div>
    </section>
  );
};

export default ContactForm;
