import React from 'react';
import { motion } from 'framer-motion';
import styles from './Main.module.css';

const HouseCard = ({ house }) => (
  <motion.div
    className={styles.houseCard}
    initial={{ y: 100, opacity: 0 }}
    whileInView={{ y: 0, opacity: 1 }}
    viewport={{ once: true, margin: "0px 0px -100px 0px" }}
    transition={{ duration: 0.6 }}
    whileHover={{ scale: 1.03 }}
  >
    <div className={styles.houseImage}>
      <img src={house.image} alt={house.name} />
    </div>
    <div className={styles.houseInfo}>
      <h3>{house.name}</h3>
      <p>{house.description}</p>
      <p className={styles.price}>{house.price}</p>
    </div>
  </motion.div>
);

const HousesSection = () => {
  return (
    <section id="houses" className={styles.housesSection}>
      <h2>Дома в продаже</h2>
      <div className={styles.housesGrid}>
        {houses.map(house => (
          <div key={house.id} className={styles.houseCard}>
            <div className={styles.houseImage}>
              <img src={house.image} alt={house.name} />
            </div>
            <div className={styles.houseInfo}>
              <h3>{house.name}</h3>
              <p>{house.description}</p>
              <p className={styles.price}>{house.price}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default HousesSection;
