import { motion } from 'framer-motion';
import styles from './Main.module.css';

// Добавляем данные о домах прямо в компонент
const houses = [
  {
    id: 1,
    name: "Дом у озера",
    description: "Просторный двухэтажный дом с видом на озеро. 150 кв.м, 4 спальни, 2 санузла.",
    price: "12 500 000 ₽",
    image: "/images/house1.jpg"
  },
  {
    id: 2,
    name: "Лесной дом",
    description: "Уютный одноэтажный дом в лесной зоне. 90 кв.м, 2 спальни, 1 санузел.",
    price: "8 200 000 ₽",
    image: "/images/house2.jpg"
  }
];

const HouseCard = ({ house }) => (
  <motion.div
    className={styles.houseCard}
    initial={{ y: 100, opacity: 0 }}
    whileInView={{ y: 0, opacity: 1 }}
    viewport={{ once: true, margin: "0px 0px -100px 0px" }}
    transition={{ duration: 0.6 }}
    whileHover={{ scale: 1.03 }}
  >
    <div className={styles.houseImage}>
      <img src={house.image} alt={house.name} />
    </div>
    <div className={styles.houseInfo}>
      <h3>{house.name}</h3>
      <p>{house.description}</p>
      <p className={styles.price}>{house.price}</p>
    </div>
  </motion.div>
);

const HousesSection = () => {
  return (
    <section id="houses" className={styles.housesSection}>
      <h2>Дома в продаже</h2>
      <div className={styles.housesGrid}>
        {houses.map(house => (
          <HouseCard key={house.id} house={house} />
        ))}
      </div>
    </section>
  );
};

export default HousesSection;
