import React, { useEffect, useRef, useState } from 'react';
import styles from './Header.module.css';

const Header = () => {
  const [showHeader, setShowHeader] = useState(true);
  const lastScrollY = useRef(window.scrollY);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      if (currentScrollY < lastScrollY.current) {
        setShowHeader(true); // Скролл вверх — показать
      } else if (currentScrollY > lastScrollY.current && currentScrollY > 80) {
        setShowHeader(false); // Скролл вниз — скрыть
      }
      lastScrollY.current = currentScrollY;
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`${styles.header} ${!showHeader ? styles.hide : ''}`}>
      <div className={styles.headerContent}>
        <img src="https://4.downloader.disk.yandex.ru/preview/a23ff0239f551fe0be6b536e48b97e0ab3819e9c7995c55686c9b91d0b613413/inf/okFBL2uzmy6Nwhmnw5-R0qnD_ezGIi5xj53p8q5ai2ESs4UCA-dR1hHg0kkv5Iqs8NEjjVQkHZAfv0fj2cDP2A%3D%3D?uid=338982262&filename=logo.jpg&disposition=inline&hash=&limit=0&content_type=image%2Fjpeg&owner_uid=338982262&tknv=v3&size=1920x940" width="70" height="70" alt="house" />
        <h1 className={styles.logo}>LUXURY ESTATE</h1>
        <nav className={styles.nav}>
          <a href="#about" className={styles.ha}>
            <button className={styles.hbuttons}>О поселке</button>
          </a>
          <a href="#location" className={styles.ha}>
            <button className={styles.hbuttons}>Расположение</button>
          </a>
          <a href="#houses" className={styles.ha}>
            <button className={styles.hbuttons}>Дома</button>
          </a>
          <a href="#contact" className={styles.ha}>
            <button className={styles.hbuttons}>Контакты</button>
          </a>
        </nav>
      </div>
    </header>
  );
};

export default Header;
