import React, { useEffect, useRef, useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import styles from './Header.module.css';

const Header = () => {
  const { scrollY } = useScroll();
  const yOffset = useTransform(scrollY, [0, 150], [0, -100]); // Подъем вверх
  const opacity = useTransform(scrollY, [50, 100], [1, 0]); // Постепенное исчезновение
  const [showHeader, setShowHeader] = useState(true);
  const lastScrollY = useRef(window.scrollY);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      if (currentScrollY > lastScrollY.current && currentScrollY > 80) {
        setShowHeader(false); // Скролл вниз — скрыть
      } else if (currentScrollY < lastScrollY.current) {
        setShowHeader(true); // Скролл вверх — показать
      }
      lastScrollY.current = currentScrollY;
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <motion.header className={`${styles.header} ${!showHeader ? styles.hide : ''}`}
      style={{ 
        y: yOffset,
        opacity
      }}
      transition={{ type: 'spring', damping: 20 }}
    >
      <motion.div className={styles.headerContent}
        style={{ opacity: useTransform(scrollY, [0, 120], [1, 0]) }}
      >
        <img src="https://4.downloader.disk.yandex.ru/preview/a23ff0239f551fe0be6b536e48b97e0ab3819e9c7995c55686c9b91d0b613413/inf/okFBL2uzmy6Nwhmnw5-R0qnD_ezGIi5xj53p8q5ai2ESs4UCA-dR1hHg0kkv5Iqs8NEjjVQkHZAfv0fj2cDP2A%3D%3D?uid=338982262&filename=logo.jpg&disposition=inline&hash=&limit=0&content_type=image%2Fjpeg&owner_uid=338982262&tknv=v3&size=1920x940" width="70" height="70" alt="house"></img>
        <h1 className={styles.logo}>LUXURY ESTATE</h1>
        <nav className={styles.nav}>
          <a href="#about">О поселке</a>
          <a href="#location">Расположение</a>
          <a href="#houses">Дома</a>
          <a href="#contact">Контакты</a>
        </nav>
      </motion.div>
    </motion.header>
  );
};

export default Header;
